import requests

def get_usdc_price():
    url = 'https://api.coingecko.com/api/v3/simple/price?ids=usd-coin&vs_currencies=usd'
    try:
        response = requests.get(url)
        data = response.json()
        print(f'Current USDC Price: ${data["usd-coin"]["usd"]}')
    except Exception as e:
        print('Error fetching price')

get_usdc_price()
